"use client";

import { AnimatedIconProps } from "./types";
import { motion, useAnimate } from "motion/react";

const EyeIcon = ({
  size = 24,
  color = "currentColor",
  strokeWidth = 2,
  className = "",
}: AnimatedIconProps) => {
  const [scope, animate] = useAnimate();

  const startAnimate = async () => {
    // Pupil contracts (blink effect)
    animate(
      ".eye-pupil",
      {
        scale: 0.7,
      },
      {
        duration: 0.15,
        ease: "easeOut",
      },
    );

    // Eye shape slightly narrows
    animate(
      ".eye-shape",
      {
        scaleY: 0.9,
      },
      {
        duration: 0.15,
        ease: "easeOut",
      },
    );
  };

  const endAnimate = async () => {
    animate(
      ".eye-pupil",
      {
        scale: 1,
      },
      {
        duration: 0.2,
        ease: "easeInOut",
      },
    );

    animate(
      ".eye-shape",
      {
        scaleY: 1,
      },
      {
        duration: 0.2,
        ease: "easeInOut",
      },
    );
  };

  return (
    <motion.svg
      ref={scope}
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      stroke={color}
      strokeWidth={strokeWidth}
      strokeLinecap="round"
      strokeLinejoin="round"
      className={`cursor-pointer ${className}`}
      onHoverStart={startAnimate}
      onHoverEnd={endAnimate}
    >
      <path stroke="none" d="M0 0h24v24H0z" fill="none" />

      {/* Pupil */}
      <motion.path
        d="M10 12a2 2 0 1 0 4 0a2 2 0 0 0 -4 0"
        className="eye-pupil"
        style={{ transformOrigin: "50% 50%" }}
      />

      {/* Eye shape */}
      <motion.path
        d="M21 12c-2.4 4 -5.4 6 -9 6c-3.6 0 -6.6 -2 -9 -6c2.4 -4 5.4 -6 9 -6c3.6 0 6.6 2 9 6"
        className="eye-shape"
        style={{ transformOrigin: "50% 50%" }}
      />
    </motion.svg>
  );
};

export default EyeIcon;

export interface AnimatedIconProps {
  /** Icon size in pixels or CSS string */
  size?: number | string;
  /** Icon color (defaults to currentColor) */
  color?: string;
  /** SVG stroke width */
  strokeWidth?: number;
  /** Additional CSS classes */
  className?: string;
}
