import { AnimatedIconProps } from "./types";
import { motion, useAnimate } from "motion/react";

const TerminalIcon = ({
  size = 24,
  color = "currentColor",
  strokeWidth = 2,
  className = "",
}: AnimatedIconProps) => {
  const [scope, animate] = useAnimate();

  const hoverAnimation = async () => {
    animate(
      ".cursor-line",
      { opacity: [1, 0, 1, 0, 1] },
      { duration: 0.8, ease: "easeInOut" },
    );
    animate(
      ".terminal-chevron",
      { x: [0, 2, 0] },
      { duration: 0.3, ease: "easeInOut" },
    );
  };

  const hoverEndAnimation = () => {
    animate(".cursor-line", { opacity: 1 }, { duration: 0.2, ease: "easeOut" });
    animate(".terminal-chevron", { x: 0 }, { duration: 0.2, ease: "easeOut" });
  };

  return (
    <motion.div
      ref={scope}
      onHoverStart={hoverAnimation}
      onHoverEnd={hoverEndAnimation}
      className={`inline-flex cursor-pointer items-center justify-center ${className}`}
    >
      <svg
        xmlns="http://www.w3.org/2000/svg"
        width={size}
        height={size}
        viewBox="0 0 24 24"
        fill="none"
        stroke={color}
        strokeWidth={strokeWidth}
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <path stroke="none" d="M0 0h24v24H0z" fill="none" />
        <motion.path className="terminal-chevron" d="M5 7l5 5l-5 5" />
        <motion.path className="cursor-line" d="M12 19l7 0" />
      </svg>
    </motion.div>
  );
};

export default TerminalIcon;

export interface AnimatedIconProps {
  /** Icon size in pixels or CSS string */
  size?: number | string;
  /** Icon color (defaults to currentColor) */
  color?: string;
  /** SVG stroke width */
  strokeWidth?: number;
  /** Additional CSS classes */
  className?: string;
}
